std::string markdownToHtml(const std::string& markdown) {
  // Regular expression for markdown syntax
  std::regex markdownRegex("(?i)\[(.+?)\]|\n|\\**|\\*|\\__|`");

  // Replace markdown syntax with HTML tags
  std::string html = std::regex_replace(markdown, markdownRegex, "<$1><b>$1</b>");

  // Remove unnecessary line breaks
  html = html.replace("\n", "");

  // Return the resulting HTML text
  return html;
}

int main() {
