function validateAndCountPrimes(arr) {
  const isValidArray = (arr) => {
    return arr.length === 5 && arr.every(Number.isInteger) && arr.every((num) => num > 0);
  };

   if (!isValidArray(arr)) {
    return "Invalid array";
  }

  const isPrime = (n) => {
    if (n <= 1) return false;
    for (let i = 2; i * i <= n; i++) {
      if (n % i === 0) return false;
    }
    return true;
  };

  const countPrimes = (arr) => {
    return arr.filter(isPrime).length;
  };

  const primeCount = countPrimes(arr);

  if (primeCount) {
    console.log(`The number of primes in the array is: ${primeCount}`);
  } else {
    console.log("No primes found");
  }
}

